import logo from "./logo.svg";
import "./App.css";
import Header from "./components/header";
import Footer from "./components/footer";
import Sidebar from "./components/sidebar";
import Dashboard from "./components/dashboard";
import Preview from "./components/preview_vault";
import CreateVault from "./components/createVault";
import { ToastContainer, toast } from "react-toastify";
import { useEffect, useState } from "react";
import "react-toastify/dist/ReactToastify.css";
import UserContextProvider, { UserContext } from "./contexts/user.contexts";
import Whitelist from "./components/whitelist";

function App() {
  const [section, setSection] = useState(3);

  return (
    <>
      <UserContextProvider>
        <UserContext.Consumer>
          {({account, handleConnect, handleDisonnect})=> <div className="relative flex flex-col h-[100vh]">
            <Header connect={handleConnect} disconnect={handleDisonnect} account={account}/>
            {account && <div className="flex relative flex-1">
              <Sidebar setSection={setSection} />
              <main className="flex-1 p-10 pr-[10vw]">
                {section == 0 && <Dashboard setSection={setSection} account={account} />}
                {section == 1 && <Preview />}
                {section == 2 && <CreateVault setSection={setSection} />}
                {section == 3 && <Whitelist />}
              </main>
            </div>}
            <Footer />
          </div>}
        </UserContext.Consumer>
      </UserContextProvider>
      <ToastContainer />
    </>
  );
}

export default App;
