import logo from "./logo.svg";
import "./App.css";
import Header from "./components/header";
import Footer from "./components/footer";
import Sidebar from "./components/sidebar";
import Dashboard from "./components/dashboard";
import Preview from "./components/preview_vault";
import CreateVault from "./components/createVault";
import { ToastContainer, toast } from "react-toastify";
import { useEffect, useState } from "react";
import "react-toastify/dist/ReactToastify.css";
import UserContextProvider from "./contexts/user.contexts";

function App() {
  const [section, setSection] = useState(2);

  return (
    <>
      <UserContextProvider >
        <div className="relative flex flex-col h-[100vh]">
          <Header />
          <div className="flex relative flex-1">
            <Sidebar setSection={setSection} />
            <main className="flex-1 p-10 pr-[10vw]">
              {section == 0 && <Dashboard setSection={setSection} />}
              {section == 1 && <Preview />}
              {section == 2 && <CreateVault setSection={setSection} />}
            </main>
          </div>
          <Footer />
        </div>
      </UserContextProvider>
      <ToastContainer />
    </>
  );
}

export default App;
