import React, {useRef, useState} from "react";
import Button from "./button";
import Input from "./input";
import Text from "./texts";
import UploadMedia from "./uploadImage";
import Web2Metadata from "./web2Metadata";
import { web2_form, web3_form } from "./form";

export default function CreateVault(){
    const [blob, setBlob] = useState(null);
    const [web2Form, setWeb2Form] = useState({...web2_form});
    const [web3Form, setWeb3Form] = useState({...web3_form});
    const errors = useRef({...web3_form});
    const [stage, setStage] = useState(0);

    function next(){
        stage < 2 && setStage(stage + 1);
    }

    function back(){
        stage > 0 && setStage(stage - 1);
    }

    return (
        <div>
            <Text size={"lg"} cls={"font-semibold"}>
                Create New Vault
            </Text>

            <div className="flex items-center w-fit mx-auto my-10">
                <div className="flex flex-col items-center justify-center relative">
                    <div className="h-[20px] w-[20px] rounded-xl bg-theme-red relative">

                    </div>
                    <Text cls={"absolute bottom-[-30px] text-[12px] w-[100px] text-theme-red font-bold"}>
                        Upload Media
                    </Text>
                </div>
                <div className="w-[200px] h-[3px] bg-theme-grey">

                </div>
                <div className="flex flex-col items-center relative">
                    <div className="h-[15px] w-[15px] rounded-xl bg-theme-grey relative">

                    </div>
                    <Text cls={"absolute bottom-[-30px] text-[12px] w-[100px]"}>
                        Web2 Metadata
                    </Text>
                </div>
                <div className="w-[200px] h-[3px] bg-theme-grey">

                </div>
                <div className="flex flex-col items-center relative">
                    <div className="h-[15px] w-[15px] rounded-xl bg-theme-grey relative">

                    </div>
                    <Text cls={"absolute bottom-[-30px] text-[12px] w-[100px]"}>
                        Web3 Metadata
                    </Text>
                </div>
            </div>


            <div className="w-[360px] mx-auto my-[100px]">
                {stage == 0 && <UploadMedia setBlob={setBlob} blob={blob}/>}
                {stage == 1 && <Web2Metadata form={web2Form} setForm={setWeb2Form}/>}
                {stage == 2 && <Web2Metadata form={web2Form} setForm={setWeb2Form}/>}

            </div>

            <div className="flex justify-between w-[260px] m-auto">
                <Button label={"Back"} onClick={back}/>
                <Button label={"Next"} onClick={next}/>
            </div> 
        </div>
    );
}
