import React, { useContext, useEffect, useState } from "react";
import Chart from "../assets/chart.svg";
import Text from "./texts";
import Track_1 from "../assets/track_1.svg";
import { toast } from "react-toastify";
import { getMetadata, getVaults } from "../api/api";
import { UserContext } from "../contexts/user.contexts";

export default function Dashboard({ account, setSection }) {
  const { vaults, setVaults } = useContext(UserContext);
  const [loaded, load] = useState(false);

  function loadVaults() {
    toast.promise(
      async () => {
        try {
          return await getVaults(account);
        } catch (error) {
          console.log(error);
          throw error;
        }
      },
      {
        pending: "Loading your Vaults",
        success: {
          render({ data }) {
            console.log(data);
            setVaults(data.metadatas);
            return `Success!`;
          },
        },
        error: {
          render({ data }) {
            return "Oops! Something went wrong";
          },
        },
      }
    );
  }

  useEffect(() => {
    if (!loaded) {
      load(true);
      return;
    }
    loadVaults();
  }, [loaded]);

  return (
    <div>
      <div className="flex">
        <div className="flex items-center w-fit ml-auto">
          <div>
            <Text cls={`text-[11px]`}>Total Streams</Text>
            <Text cls={`font-bold`}>120,000</Text>
          </div>
          <div className="bg-theme-red w-fit h-[25px] rounded p-1 relative ml-3">
            <img src={Chart} className={`h-[100%]`} />
          </div>
        </div>
      </div>

      <div>
        <div className="mb-5">
          <Text size={"lg"} cls={"font-bold"}>
            TRACKS
          </Text>
        </div>
        <div>
          {vaults &&
            vaults.map((vault, index) => (
              <Track
                title={vault.mediaTitle}
                desc={vault.description}
                link={vault.ipfsUrl}
                key={index}
                streams={vault.streamCount}
              />
            ))}
          {/* <Track />
          <Track />
          <Track /> */}
        </div>
      </div>
    </div>
  );
}

function Track({ streams, link }) {
  const [metadata, setMetadata] = useState(null);


    useEffect(()=>{
        getMetadata(link).then((meta)=>{
            setMetadata(meta);
        })
    }, [])

  if (!metadata) return null;
  return (
    <div className="flex items-center w-[40%] min-w-[500px] justify-between my-4">
      <div>
        <img src={Track_1} className={`h-[100px]`} />
      </div>

      <div className="w-[200px] h-[100px]">
        <Text size={"md"} cls={`font-bold`}>
          {metadata.name}
        </Text>

        <Text cls={"text-[11px]"}>
          {metadata.description}
        </Text>
      </div>

      <div className="flex flex-col items-center">
        <Text size={"lg"} cls={"font-bold"}>
          {streams}
        </Text>
        <Text cls={`font-semibold`}>streams</Text>
      </div>
    </div>
  );
}
